mapper.select:
SELECT 

FROM t_hotel_bank a

WHERE a.id = #{id}

SELECT 

FROM t_hotel_bank a 
INNER JOIN e_hotel e on a.hotelid = e.id



		AND e.hotelName LIKE
		concat('%',#{hotelname},'%')


		AND a.id = #{id}


		AND a.hotelid = #{hotelid}


		AND a.bank LIKE
		concat('%',#{bank},'%')


		AND a.bankbranch LIKE
		concat('%',#{bankbranch},'%')


		AND a.transfertype = #{transfertype}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.account = #{account}


		AND a.createtime = #{createtime}


		AND a.updatetime = #{updatetime}




		ORDER BY ${page.orderBy}

SELECT 

FROM t_hotel_bank a






		ORDER BY ${page.orderBy}
