mapper.select:
SELECT ,
FROM rf_crm_notice a
WHERE a.id = #{id}

SELECT 
	,

FROM rf_crm_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.notice_type = #{noticeType}


		AND a.notice_title = #{noticeTitle}


		AND a.notice_content = #{noticeContent}


		AND a.notice_top = #{noticeTop}


		AND a.notice_read = #{noticeRead}


		AND a.notice_status = #{noticeStatus}


		AND a.notice_time = #{noticeTime}


		AND a.notice_attachment = #{noticeAttachment}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.remarks = #{remarks}


		AND a.del_flag = #{delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM rf_crm_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE rf_crm_notice SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
