mapper.select:
SELECT 

FROM gds_channel a

WHERE a.id = #{id}

SELECT 

FROM gds_channel a




		AND a.id = #{id}


		AND a.channelid = #{channelid}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.state = #{state}


		AND a.is_visible = #{isVisible}


		AND a.createtime = #{createtime}


		AND a.createuser = #{createuser}


		AND a.updatetime = #{updatetime}


		AND a.updateuser = #{updateuser}




		ORDER BY ${page.orderBy}

SELECT 

FROM gds_channel a






		ORDER BY ${page.orderBy}
