mapper.select:
SELECT 

FROM hotel_channel_setting a

WHERE a.id = #{id}

SELECT 

FROM hotel_channel_setting a




		AND b.hotelname LIKE 
			'%'||#{hotelname}||'%'
			'%'+#{hotelname}+'%'
			concat('%',#{hotelname},'%')



		AND a.hotelid = #{hotelid}




		AND a.channelid = #{channelid}


		AND a.state = #{state}


		 AND date_format(a.createtime,'%Y-%m-%d') = date_format(#{createtime},'%Y-%m-%d')


		 AND date_format(a.updatetime,'%Y-%m-%d') = date_format(#{updatetime},'%Y-%m-%d')




		ORDER BY ${page.orderBy}

SELECT 

FROM hotel_channel_setting a






		ORDER BY ${page.orderBy}
