mapper.select:
SELECT 

FROM hotel_mapping a

WHERE a.id = #{id}

SELECT 

FROM hotel_mapping a




		AND a.hotelid = #{hotelid}


		AND a.hotelname LIKE 
			'%'||#{hotelname}||'%'
			'%'+#{hotelname}+'%'
			concat('%',#{hotelname},'%')


		AND a.hoteladdr LIKE 
			'%'||#{hoteladdr}||'%'
			'%'+#{hoteladdr}+'%'
			concat('%',#{hoteladdr},'%')


		AND a.channelid = #{channelid}


		AND a.hotelphone = #{hotelphone}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}

SELECT 

FROM hotel_mapping a






		ORDER BY ${page.orderBy}
