mapper.select:
SELECT 

FROM rack_distribute_price a

WHERE a.id = #{id}

SELECT 

FROM rack_distribute_price a

WHERE a.hotelid = #{hotelid} and  a.roomtypeid = #{roomtypeid}

SELECT 

FROM rack_distribute_price a




		AND b.hotelname LIKE 
			'%'||#{hotelname}||'%'
			'%'+#{hotelname}+'%'
			concat('%',#{hotelname},'%')


		AND a.createtime = #{createtime}


		AND a.createuser = #{createuser}


		AND a.updatetime = #{updatetime}


		AND a.updateuser = #{updateuser}




		ORDER BY ${page.orderBy}

SELECT 

FROM rack_distribute_price a






		ORDER BY ${page.orderBy}
