SELECT 

FROM roomtype a




		AND a.id = #{id}


		AND a.id_1 = #{id1}


		AND a.hotelid = #{hotelid}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.roomtypepms = #{roomtypepms}


		AND a.roomnum = #{roomnum}


		AND a.cost = #{cost}


		AND a.type = #{type}


		AND a.updatetime = #{updatetime}




		ORDER BY ${page.orderBy}

