mapper.select:
SELECT 

FROM oms_receipt a

WHERE a.id = #{id}

		SELECT 

		FROM oms_receipt a


		    (is_open = 1 or is_open = 2)

				AND a.title LIKE 
					'%'||#{title}||'%'
					'%'+#{title}+'%'
					concat('%',#{title},'%')


				AND a.identi_id = #{identiid}


				AND a.receipt_code = #{receiptcode}


                AND a.rtype = #{rtype}


                AND a.is_open = #{isopen}




				ORDER BY ${page.orderBy}

SELECT 

FROM oms_receipt a






		ORDER BY ${page.orderBy}
