mapper.select:
SELECT 

FROM settlement_psbrules a

WHERE a.id = #{id}

SELECT 

FROM settlement_psbrules a




		AND a.id = #{id}


		AND a.psbid = #{psbid}


		AND a.rulename LIKE 
			'%'||#{rulename}||'%'
			'%'+#{rulename}+'%'
			concat('%',#{rulename},'%')


		AND a.fee10 = #{fee10}


		AND a.fee20 = #{fee20}


		AND a.remarks = #{remarks}


		AND a.createtime >= #{beginCreatetime}


		AND a.createtime <= #{endCreatetime}


		AND a.updatetime >= #{beginUpdatetime} 


		AND a.updatetime <= #{endUpdatetime}


		AND a.psbname LIKE 
			'%'||#{psbname}||'%'
			'%'+#{psbname}+'%'
			concat('%',#{psbname},'%')


		AND a.delflag = #{delflag}


		AND a.delflag = 1




		ORDER BY ${page.orderBy}
