mapper.select:
SELECT 

FROM settlement_bill_info a

WHERE a.id = #{id}

SELECT 

FROM settlement_bill_info a



		AND a.id = #{id}


		AND a.billid = #{billid}


		AND a.billscsum >= #{beginBillscsum}


		AND a.billscsum <= #{endBillscsum}


		AND a.settlesum >= #{beginSettlesum}


		AND a.settlesum <= #{endSettlesum}


		AND a.settletime >= #{beginSettletime}


		AND a.settletime <= #{endSettletime}


		AND a.status = #{status}


		AND a.remarks LIKE 
			'%'||#{remarks}||'%'
			'%'+#{remarks}+'%'
			concat('%',#{remarks},'%')


		AND a.createtime >= #{beginCreatetime}


		AND a.createtime <= #{endCreatetime}


		AND a.updatetime >= #{beginUpdatetime}


		AND a.updatetime <= #{endUpdatetime}


ORDER BY id


		, ${page.orderBy}




DESC

SELECT 

FROM settlement_bill_info a






		ORDER BY ${page.orderBy}
